<button type="button" x-data="{ enabled: @entangle($attributes->wire('model')) }" x-bind:class="{ 'bg-primary-600': enabled, 'bg-gray-200': !enabled }" x-on:click.debounce="enabled = !enabled" class="relative inline-flex flex-shrink-0 h-6 w-11 border-2 border-transparent rounded-full cursor-pointer transition-colors ease-in-out duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500">
    <span x-bind:class="{ 'translate-x-5': enabled, 'translate-x-0': !enabled }" class=" pointer-events-none inline-block h-5 w-5 rounded-full bg-white shadow transform ring-0 transition ease-in-out duration-200"></span>
</button>